"use strict";
var Resolvable = function () {
	this.isOrigin = true;
};

Resolvable.prototype.resolve = function (runner, ctx) {
	throw new Error('Abstract method call');
};

Resolvable.prototype.ensureNotOrigin = function () {
	return this.isOrigin ? this.clone() : this;
};

Resolvable.prototype.clone = function () {
	var result = new this.constructor();
	for (var k in this) {
		if (this.hasOwnProperty(k)) {
			result[k] = this[k];
		}
	}
	return result;
};


module.exports = Resolvable;
