"use strict";
var inherits = require('util').inherits;
var Resolvable = require('./core/resolvable');


var Custom = function (f) {
	Resolvable.call(this);
	this.f = f;
};
inherits(Custom, Resolvable);

Custom.prototype.resolve = function (runner, ctx) {
	return this.f(runner, ctx);
};

Custom.custom = function (f) {
	return new Custom(f);
};


module.exports = Custom;
