"use strict";
var inherits = require('util').inherits;
var WrappedTask = require('./core/wrapped_task');


var Box = function (k, v) {
	WrappedTask.call(this);
	this.k = k;
	this.v = v;
};
inherits(Box, WrappedTask);

Box.prototype.run = function (runner, ctx, cb) {
	ctx.box[this.k] = this.v;
	cb();
};

Box.toBox = function (k, v) {
	return new Box(k, v);
};


module.exports = Box;
