"use strict";
var inherits = require('util').inherits;
var WrappedTask = require('./core/wrapped_task');


var Print = function (args) {
	WrappedTask.call(this);
	this.args = args;
};
inherits(Print, WrappedTask);

Print.prototype.run = function (runner, ctx, cb) {
	var s = runner.joinStr(this.args, ctx);
	runner.printStr(s);
	cb();
};

Print.print = function () {
	return new Print(Array.prototype.slice.call(arguments));
};


module.exports = Print;
