"use strict";
var inherits = require('util').inherits;
var WrappedTask = require('./core/wrapped_task');


var Result = function (args) {
	WrappedTask.call(this);
	this.args = args;
};
inherits(Result, WrappedTask);

Result.prototype.run = function (runner, ctx, cb) {
	if (this.args.length <= 1) {
		cb(null, this.args[0]);
	}
	else {
		cb.apply(null, [null].concat(this.args));
	}
};

Result.result = function () {
	return new Result(Array.prototype.slice.call(arguments));
};


module.exports = Result;
